import React, {Component} from "react";
import '../../node_modules/bootstrap/dist/css/bootstrap.min.css';
import TodoConsumer from "../context";
import TodoItemComponent from "./TodoItemComponent";


class Todo extends Component {

    constructor(props) {
        super(props);
        this.state = {
            isVisible: false
        }

    }

    handleClick = () => {
        this.setState({
            isVisible: !this.state.isVisible
        })
    };

    render() {
        return (
            <div className="card p-5">
                <div className="card-header" onClick={this.handleClick}>
                    {this.props.name}
                    <i className="far fa-trash-alt float-sm-right" style={{cursor: 'pointer'}}/>
                </div>

                {this.state.isVisible ? <TodoConsumer>
                {
                    value => {
                        const {todoItems} = value;
                        return (
                            <div className='app-container col-md-6 border border-info mt-lg-5 py-4'>
                                {
                                    todoItems.map(todoItem => {
                                        return (
                                            <TodoItemComponent
                                                key={todoItem.id}
                                                id={todoItem.id}
                                                name={todoItem.name}
                                                description={todoItem.description}
                                                status={todoItem.status}
                                                deadline={todoItem.deadline}
                                                todoListId={todoItem.todoListId}
                                                dependsOn={todoItem.dependsOn}
                                            />
                                        )
                                    })
                                }
                            </div>
                        )
                    }
                }
            </TodoConsumer> : null}
            </div>
        );
    }

}

export default Todo;
