import React, {Component} from "react";


class TodoItemComponent extends Component {

    constructor(props) {
        super(props);
        this.state = {
            showDesc: false
        }

    }

    handleClick = () => {
        this.setState({
                showDesc: !this.state.showDesc
            }
        )
    };

    render() {
        return (
            <div className="card">
                <ul className="list-group">
                    <li className="list-group-item"><i className="pr-2 far fa-trash-alt float-sm-right"
                                                       style={{cursor: 'pointer'}}
                                                       data-toggle=" tooltip" data-placement=" bottom"
                                                       title=" Delete Todo List"/>Cras justo odio
                    </li>
                </ul>
                <div className="card-header" data-toggle="tooltip"
                     data-placement="bottom" title="Show Description"
                     onClick={this.handleClick}>

                </div>
                <div className="card-body">
                    {this.state.showDesc ? <p className="card-text">{this.props.description}</p> : null}
                </div>
            </div>
        );
    }

}

export default TodoItemComponent;
