import React, {Component} from "react";


class TodoItemComponent extends Component {

    constructor(props) {
        super(props);
        this.state = {
            showDesc: false
        }

    }

    handleClick = () => {
        this.setState({
            showDesc: !this.state.showDesc
            }
        )
    }

    render() {
        return (
            <div className="card-body">
                <h5 onClick={this.handleClick} className="card-title border border-info badge-pill">{this.props.name}</h5>
                {this.state.showDesc ? <p className="card-text">{this.props.description}</p> : Node}
            </div>
        );
    }

}

export default TodoItemComponent;
