import React, {Component} from "react";


class TodoItemComponent extends Component {

    constructor(props) {
        super(props);
        this.state = {
            showDesc: false,
        }

    }


    isExpired = () => {
        let deadline = new Date(this.props.deadline);
        let now = Date.now();
        console.log(deadline);
        console.log(now);
        return now > deadline;
    };


    render() {
        return (
            <li className="list-group-item">
                <ReactModal
                    isOpen={this.props.isOpen}
                    contentLabel='modal'
                    className='recipeModal'>
                    <div className="inputArea">
                        <div className="inputContainer">
                            Recipe name <input type="text" value={this.props.tempName}
                                               onChange={this.props.handleChange} name="name"/>
                        </div>
                        <div className="inputContainer">
                            Ingredients, separated by commas <textarea rows="3" value={this.props.tempIngredients}
                                                                       onChange={this.props.handleChange}
                                                                       name="ingredients"/>
                        </div>
                    </div>
                    <div className="buttonContainer">
                        <div className="button edit" onClick={this.handleSubmit}>Save</div>
                        <div className="button delete" onClick={this.props.handleClose}>Cancel</div>
                    </div>
                </ReactModal>
                <i className="far fa-trash-alt float-sm-right"
                   style={{cursor: 'pointer'}}
                   data-toggle=" tooltip" data-placement=" bottom"
                   title=" Delete Todo"/>
                {this.props.name} {(() => {
                if (this.isExpired() && this.props.status === 'Not Completed') {
                    return (<span className="badge badge-pill badge-danger">Expired</span>)
                } else {
                    return null
                }
            })()}
                {(() => {
                    if (this.props.status === 'Completed') {
                        return <span
                            className="text-success float-sm-right pr-2">{this.props.status}</span>;
                    } else {
                        return <span
                            className="text-danger float-sm-right pr-2">{this.props.status}</span>;
                    }
                })()}

            </li>
        );
    }

}

export default TodoItemComponent;
