import React, {Component} from "react";


class TodoItemComponent extends Component {

    constructor(props) {
        super(props);
        this.state = {
            showDesc: false
        }

    }

    handleClick = () => {
        this.setState({
                showDesc: !this.state.showDesc
            }
        )
    };

    render() {
        return (
            <li className="list-group-item"><i className="far fa-trash-alt float-sm-right"
                                               style={{cursor: 'pointer'}}
                                               data-toggle=" tooltip" data-placement=" bottom"
                                               title=" Delete Todo"/>
                {this.props.status === 'Completed'} ? <span
                    className="text-success float-sm-right pr-2">{this.props.status}</span>{this.props.name} : <span
                    className="text- float-sm-right pr-2">{this.props.status}</span>{this.props.name}
            </li>

        );
    }

}

export default TodoItemComponent;
