import React, {Component} from "react";
import '../App.css'


class TodoItemComponent extends Component {

    constructor(props) {
        super(props);
        this.state = {
            showDesc: false,
            modelOpened: false
        }

    }


    isExpired = () => {
        let deadline = new Date(this.props.deadline);
        let now = Date.now();
        console.log(deadline);
        console.log(now);
        return now > deadline;
    };

    modalToggle = () => {
        this.setState({modalOpened: !this.state.modalOpened})
    };

    render() {
        const coverClass = this.state.modalOpened ? 'modal-cover modal-cover-active' : 'modal-cover'
        const containerClass = this.state.modalOpened ? 'modal-container modal-container-active' : 'modal-container'
        return (
            <li onClick={this.modalToggle} data-toggle="modal" data-target="#exampleModalCenter"
                className="list-group-item">
                <div>

                    <div className={containerClass}>
                        <div className='modal-header'/>
                        <div className='modal-body'/>
                        <div className='modal-footer'/>
                    </div>

                    <div className={coverClass} onClick={this.modalToggle}/>
                </div>
                <i className="far fa-trash-alt float-sm-right"
                   style={{cursor: 'pointer'}}
                   data-toggle=" tooltip" data-placement=" bottom"
                   title=" Delete Todo"/>
                {this.props.name} {(() => {
                if (this.isExpired() && this.props.status === 'Not Completed') {
                    return (<span className="badge badge-pill badge-danger">Expired</span>)
                } else {
                    return null
                }
            })()}
                {(() => {
                    if (this.props.status === 'Completed') {
                        return <span
                            className="text-success float-sm-right pr-2">{this.props.status}</span>;
                    } else {
                        return <span
                            className="text-danger float-sm-right pr-2">{this.props.status}</span>;
                    }
                })()}

            </li>
        );
    }

}

export default TodoItemComponent;
