import React, {Component} from "react";


class TodoItemComponent extends Component {

    constructor(props) {
        super(props);
        this.state = {
            showDesc: false
        }

    }

    handleClick = () => {
        this.setState({
                showDesc: !this.state.showDesc
            }
        )
    }

    render() {
        return (
            <div className="card-body">
                <h5 onClick={this.handleClick} className="py-2 card-title border border-info badge-light">{this.props.name}
                    <i className="pr-2 far fa-trash-alt float-sm-right" style={{cursor: 'pointer'}}
                       data-toggle=" tooltip" data-placement=" bottom" title=" Delete Todo List"/></h5>
                {this.state.showDesc ? <p className="card-text">{this.props.description}</p> : Node}
            </div>
        );
    }

}

export default TodoItemComponent;
