import React, {Component} from "react";
import moment from 'moment';


class TodoItemComponent extends Component {

    constructor(props) {
        super(props);
        this.state = {
            showDesc: false,
        }

    }

    handleClick = () => {
        this.setState({
                showDesc: !this.state.showDesc
            }
        )
    };

    isExpired = () => {
        let deadline = moment(this.props.deadline).format('YYYY-MM-DDTHH:mm:ss:sssZ');
        let now = moment.now();
        return now.diff(deadline, 'days') > 0
    };

    render() {
        return (
            <li className="list-group-item"><i className="far fa-trash-alt float-sm-right"
                                               style={{cursor: 'pointer'}}
                                               data-toggle=" tooltip" data-placement=" bottom"
                                               title=" Delete Todo"/>
                {this.props.name} {this.isExpired ? (<span className="badge badge-pill badge-danger">Expired</span>
                ) : null}
                {(() => {
                    switch (this.props.status) {
                        case 'Completed':
                            return <span
                                className="text-success float-sm-right pr-2">{this.props.status}</span>;
                        default:
                            return <span
                                className="text-danger float-sm-right pr-2">{this.props.status}</span>;
                    }
                })()}

            </li>

        );
    }

}

export default TodoItemComponent;
