import React, {Component} from "react";


class TodoItemComponent extends Component {

    constructor(props) {
        super(props);
        this.state = {
            showDesc: false
        }

    }

    handleClick = () => {
        this.setState({
                showDesc: !this.state.showDesc
            }
        )
    }

    render() {
        return (
            <div className="card p-5">
                <div className="card-header py-2 card-title border border-info badge-light" data-toggle="tooltip"
                     data-placement="bottom" title="Show Description"
                     onClick={this.handleClick}>
                    {this.props.name}
                    <i className="pr-2 far fa-trash-alt float-sm-right" style={{cursor: 'pointer'}}
                           data-toggle=" tooltip" data-placement=" bottom" title=" Delete Todo List"/>
                </div>
                <div className="card-body">
                    {this.state.showDesc ? <p className="card-text">{this.props.description}</p> : null}
                </div>
            </div>
        );
    }

}

export default TodoItemComponent;
