import React, {Component} from 'react';
import {Link} from 'react-router-dom' ;
import Home from "./homepage";


class SignInForm extends Component {

    constructor() {

        super();

        this.state = {
            username: '',
            password: '',
            successLogin: false
        };

    }

    handleChange = (e) => {
        let target = e.target;
        let value = target.type === 'checkbox' ? target.checked : target.value;
        let name = target.name;


        this.setState({
            [name]: value
        });

    };

    showLoginError = () => {
        console.log("Login Failed!")
    }

    handleSubmit = (e) => {

        return this.state.successLogin ? (
            <Home name={"Eyup"}/>
        ) : this.showLoginError();
    };


    render() {

        return (
            <div className="FormCenter">
                <form onSubmit={this.handleSubmit} className="FormField">

                    <div className="FormField">
                        <label className="FormField__Label">Username</label>
                        <input type="text" id="username" className="FormField__Input"
                               placeholder="Enter your username" name="username" value={this.state.username}
                               onChange={this.handleChange}/>
                    </div>

                    <div className="FormField">
                        <label className="FormField__Label" htmlFor="password">Password</label>
                        <input type="password" id="password" className="FormField__Input"
                               placeholder="Enter your password" name="password" value={this.state.password}
                               onChange={this.handleChange}/>
                    </div>

                    <div className="FormField">
                        <button className="FormField__Button mr-20">Sign In</button>
                        <Link to="/"
                              className="FormField__Link"> Create an account</Link>
                    </div>
                </form>
            </div>
        )
    }
}

export default SignInForm
