import React, {Component} from "react";
import Modal from './Modal'


class TodoItemComponent extends Component {

    constructor(props) {
        super(props);
        this.state = {
            showDesc: false,
            isOpen: false
        }

    }


    isExpired = () => {
        let deadline = new Date(this.props.deadline);
        let now = Date.now();
        console.log(deadline);
        console.log(now);
        return now > deadline;
    };

    toggleModal = () => {
        this.setState({
            isOpen: !this.state.isOpen
        });
    };

    render() {
        return (
            <li onClick={this.toggleModal} onClose={this.toggleModal} className="list-group-item">
                <div>
                    <Modal show={this.state.isOpen} onClose={this.toggleModal}>Modal Content</Modal>
                </div>
                <i className="far fa-trash-alt float-sm-right"
                   style={{cursor: 'pointer'}}
                   data-toggle=" tooltip" data-placement=" bottom"
                   title=" Delete Todo"/>
                {this.props.name} {(() => {
                if (this.isExpired() && this.props.status === 'Not Completed') {
                    return (<span className="badge badge-pill badge-danger">Expired</span>)
                } else {
                    return null
                }
            })()}
                {(() => {
                    if (this.props.status === 'Completed') {
                        return <span
                            className="text-success float-sm-right pr-2">{this.props.status}</span>;
                    } else {
                        return <span
                            className="text-danger float-sm-right pr-2">{this.props.status}</span>;
                    }
                })()}

            </li>
        );
    }

}

export default TodoItemComponent;
