import React, {Component} from 'react';
import '../App.css'
import {
    Container, Col, Form,
    FormGroup, Label, Input,
    Button,
} from 'reactstrap';

class SignUpForm extends Component {

    constructor(props) {
        super(props);
        this.state = {
            first_name: '',
            last_name: '',
            username: '',
            password: ''
        }
    }

    handleChange = (e) => {
        let target = e.target;
        let value = target.type === 'checkbox' ? target.checked : target.value;
        let name = target.name;

        console.log(target);


        this.setState({
            [name]: value

        });
    };

    handleClick = (e) => {
        console.log("TEST")
    };

    render() {
        return (
            <Container className="App justify-content-center col-md-3 border border-info mt-lg-5 py-5">
                <h2>Sign Up</h2>
                <Form className="form">
                    <Col>
                        <FormGroup>
                            <Label>Name</Label>
                            <Input
                                type="text"
                                name="name"
                                id="exampleName"
                                placeholder="Type your first name"
                            />
                        </FormGroup>
                    </Col>
                    <Col>
                        <FormGroup>
                            <Label>Surname</Label>
                            <Input
                                type="text"
                                name="surname"
                                id="exampleSurname"
                                placeholder="Type your last name"
                            />
                        </FormGroup>
                    </Col>
                    <Col>
                        <FormGroup>
                            <Label>Username</Label>
                            <Input
                                type="text"
                                name="username"
                                id="exampleUsername"
                                placeholder="Type your username"
                            />
                        </FormGroup>
                    </Col>
                    <Col>
                        <FormGroup>
                            <Label for="examplePassword">Password</Label>
                            <Input
                                type="password"
                                name="password"
                                id="examplePassword"
                                placeholder="********"
                            />
                        </FormGroup>
                    </Col>
                    <Button className='btn btn-outline-info btn-light'>Register</Button>
                </Form>
            </Container>
        );

    }
}


export default SignUpForm;
