import React, {Component} from "react";


class TodoItemComponent extends Component {

    constructor(props) {
        super(props);
        this.state = {
            showDesc: false,
        }

    }

    handleClick = () => {
        this.setState({
                showDesc: !this.state.showDesc
            }
        )
    };

    isExpired = () => {
        let deadline = new Date(this.props.deadline);
        let now = Date.now();
        console.log(deadline);
        console.log(now);
        console.log(now.diff(deadline, 'days'));
        return now > deadline;
    };

    render() {
        return (
            <li className="list-group-item"><i className="far fa-trash-alt float-sm-right"
                                               style={{cursor: 'pointer'}}
                                               data-toggle=" tooltip" data-placement=" bottom"
                                               title=" Delete Todo"/>
                {this.props.name} {(() => {
                    if (this.isExpired()) {
                        return (<span className="badge badge-pill badge-danger">Expired</span>)
                    } else {
                        return (<span className="badge badge-pill badge-danger">Expired</span>)
                    }
                })}
                {(() => {
                    if (this.props.status === 'Completed') {
                        return <span
                            className="text-success float-sm-right pr-2">{this.props.status}</span>;
                    } else {
                        return <span
                            className="text-danger float-sm-right pr-2">{this.props.status}</span>;
                    }
                })()}

            </li>

        );
    }

}

export default TodoItemComponent;
